import { Dictionary } from '@aiot-toolkit/shared-utils';
import CompileMode from '../enum/CompileMode';
/**
 * 编译参数
 */
export default interface ICompileParam extends Dictionary<any> {
    /**
     * 项目路径
     */
    projectPath: string;
    /**
     * 打包模式，开发环境/生产环境
     */
    mode: CompileMode;
    platform: string;
    /**
     * 打包来源
     *
     * cmd：命令行
     * quickapp-ide：快应用 IDE
     * 其它自定义值
     */
    originType?: string;
}
