/**
 * IChunk
 */
export type IFileHash = {
    /**
     * 文件名
     */
    name: string;
    /**
     * 文件生成的  SHA256 格式hash
     */
    hash: Buffer;
};
export type IChunkOptions = {
    files: IFileHash[];
};
export type IPartChunk = {
    tag: boolean;
    startIndex: number;
    len: number;
    previous: number;
    sign?: Buffer;
};
export interface IChunk {
    signchunk: Buffer;
    tag: boolean;
    length: number;
    options: IChunkOptions;
    sections: {
        header: IPartChunk;
        central: IPartChunk;
        footer: IPartChunk;
    };
}
