import { Dictionary } from '@aiot-toolkit/shared-utils';
/**
 * vela manifest文件对应的数据结构
 */
export default interface IManifest {
    package: string;
    name?: string;
    versionName?: string;
    versionCode?: number;
    minPlatformVersion?: number;
    deviceId?: string;
    deviceTypeList?: string[];
    features?: IFeatures[];
    config?: {
        logLevel: string;
        designWidth?: string | number;
    };
    router: {
        entry: string;
        pages: Dictionary<{
            component?: string;
        }>;
    };
    minAPILevel?: number;
    packageInfo?: Dictionary<string | number>;
    icon: string;
}
export interface IFeatures {
    name: string;
}
