/**
 * 参数基础结构
 */
interface IParam {
    /**
     * 参数名
     */
    name: string;
    /**
     * 描述，为保证规范，设置为必填
     */
    description: string;
    /**
     * 默认值
     */
    defaultValue?: any;
    /**
     * 是否启用交互式询问
     *
     * 如果启用 且 未在命令中填写；则会进行询问
     */
    enableInquirer?: boolean;
    /**
     * 废弃信息， 如果有值，表示此参数已废弃
     */
    deprecated?: string;
    /**
     * 参数废弃的版本
     */
    deprecatedVersion?: string;
}
/**
 * 选项参数
 */
interface ChoiceParam extends IParam {
    choices: {
        value: string | number;
        name?: string;
        description?: string;
    }[];
}
/**
 * 输入文本参数
 */
interface InputParam extends IParam {
    type?: 'string';
    /**
     * 校验参数值
     * @param value
     * @returns
     */
    validate?: (value: string) => boolean | string | Promise<boolean | string>;
}
/**
 * boolean 参数
 */
interface ConfirmParam extends IParam {
    type: 'confirm';
}
/**
 * 单选参数
 */
interface SelectParam extends ChoiceParam {
    type: 'select';
}
/**
 * 多选参数
 */
interface CheckboxParam extends ChoiceParam {
    type: 'checkbox';
}
type ParamType = InputParam | SelectParam | CheckboxParam | ConfirmParam;
export default ParamType;
