import IOffset from './IOffset';
/**
 * IParser
 */
export interface ParserResult<AST> {
    /**
     * 语法树
     */
    ast: AST;
    /**
     * 偏移量列表
     */
    offsetList?: Array<IOffset>;
}
export default interface IParser<AST> {
    /**
     * 解析源码为：ast树、偏移列表
     * @param content
     * @param fileName
     */
    parser(content: string, fileName: string): ParserResult<AST> | Promise<ParserResult<AST>>;
}
