import { MapData } from '@aiot-toolkit/shared-utils';
import { SourceMapGenerator } from 'source-map';
import IOffset from './IOffset';
/**
 * ITranslate
 */
export default interface ITranslate<SOURCE, TARGET> {
    translate(sourceTree: SOURCE, offsetList: IOffset[]): {
        targetTree: TARGET;
        mapList: MapData[];
        sourceMap?: SourceMapGenerator;
    } | Promise<{
        targetTree: TARGET;
        mapList: MapData[];
        sourceMap?: SourceMapGenerator;
    }>;
}
