import { Dictionary, Logger } from '@aiot-toolkit/shared-utils';
interface IElement {
    /**
     * 别名
     *
     * 如设置别名，则生成时会将tag 名称转换为 别名
     *
     * @example <img /> 生成为 image
     */
    aliasName?: string;
    /**
     * 允许文字子节点
     *
     * + true 表示允许，此时文本子节点内容将被设置为 value 属性的值
     * + string 值表示允许，且需要设置为指定的属性名
     */
    allowTextChildren?: boolean | string;
    /**
     * 属性
     */
    attributes?: Dictionary<IAttribute>;
    /**
     * 事件列表
     */
    events?: string[];
}
interface IAttribute {
    /**
     * 强制是动态值，例如 if
     */
    forceDynamic?: boolean;
    /**
     * 是否必填
     */
    required?: boolean;
    /**
     * 枚举值，如果有，则属性值必须存在于枚举值中
     */
    enums?: string[];
    /**
     * 校验方法
     * @param value
     * @returns
     */
    validate?: (value: string, onLog: Logger) => string[];
}
export { IAttribute, IElement };
