import { IPosition } from '@aiot-toolkit/shared-utils';
/**
 * 标识符节点
 */
export interface IIdentifierNode {
    name: string;
    location?: IPosition;
}
/**
 * 属性节点
 */
export interface IAttributeNode<V = any> {
    name: string;
    valueUnset?: boolean;
    value: V;
    nameLocation?: IPosition;
    /**
     * 值的位置，因为 parse5工具限制，目前此属性无值
     */
    valueLocation?: IPosition;
}
