/**
 * IStyleAst
 */
import { IPosition } from '@aiot-toolkit/shared-utils';
import { Selector } from '../enum/StyleSelectorType';
type RecordSelectorList = (number | string)[][];
interface StyleItem {
    [key: string]: number | string | string[] | (number | string)[] | StyleItem;
}
type IStyleNode = (StyleItem | (number | string)[][])[];
type PreludeItem = {
    type: string;
    name: string;
};
type PreludeList = PreludeItem[];
type SimpleValueType = {
    value: string;
    unit: string;
    position?: IPosition;
};
type ComplexValueType = {
    name: string;
    param: {
        type: string;
        value: string;
    }[];
    position?: IPosition;
};
type ValueType = SimpleValueType | ComplexValueType;
type BlockItem = {
    name: string;
    value: ValueType[];
    position?: IPosition;
};
type AtRuleType = {
    type: string;
    name: string;
    prelude: PreludeList[];
    rules: (NoPreludeAtRule | RuleType | AtRuleType)[];
    position?: IPosition;
};
type NoPreludeAtRule = {
    type: string;
    name: string;
    block: BlockItem[];
    position?: IPosition;
};
type RuleType = {
    type: string;
    /**
     * 选择器列表
     */
    prelude: PreludeList[];
    /**
     * 样式列表，每项表示1个属性
     */
    block: BlockItem[];
    position?: IPosition;
};
type IRuleType = AtRuleType | NoPreludeAtRule | RuleType;
interface IStyleAst {
    styleContent: IRuleType[];
    nodeMap?: IPosition[];
}
export { AtRuleType, BlockItem, IRuleType, IStyleAst, IStyleNode, NoPreludeAtRule, PreludeItem, PreludeList, RecordSelectorList, RuleType, Selector, StyleItem, ValueType, SimpleValueType, ComplexValueType };
