/**
 * ITranslateOption
 */
export default interface ITranslateOption {
    enableProtobuf?: boolean;
    /**
     * 相对项目根目录的路径，例如`src`
     */
    sourceRoot: string;
    enableE2e?: boolean;
    enableStats?: boolean;
    optimizeCssAttr?: boolean;
    style?: {
        /**
         * 自动补齐 @media 条件的方法， 默认为 start
         *
         * all-在所有 @media 条件中加上 screen and
         * start-在第1个 @media 条件中加上 screen and
         * false-不处理 @media 条件
         */
        autoPatchMediaCondition: 'all' | 'start' | false;
    };
}
