import StyleSelectorName from '../../enum/android/StyleSelectorName';
type StyleValue = string | number | Object | Array<any>;
/**
 * 普通样式
 * @example
 * ```json
 * ".a": {
 *  color: "red"
 * }
 * ```
 */
type AndroidStyleItem = Record<string, Record<string, StyleValue>>;
/**
 * font-face
 *
 * @example
 * ```json
 * "@FONT-FACE":{
 *    fonta:{
 *      fontFamily:"fonta",
 *      fontName:"fonta",
 *      src: ["./a.ttf", "b.ttf"],
 *      src: ["./a.ttf", "b.ttf"]
 *    }
 * }
 * ```
 */
type AndroidFontFace = Record<StyleSelectorName.FONT_FACE, Record<string, StyleValue>>;
/**
 * media
 *
 * @example
 * ```json
 * "@MEDIA":[
 *   {
 *    "condition": "screen and (min-width: 500px)",
 *    ".b": {
 *      "color": "#FF0000"
 *    },
 *    ".a": {
 *      "color": "#FF0000"
 *    }
 *  }
 * ]
 * ```
 */
type AndroidMedia = Record<StyleSelectorName.MEDIA, Array<{
    condition: string;
    [key: string]: StyleValue;
}>>;
/**
 * keyframes
 *
 * @example
 * "@KEYFRAMES": {
 *  aaa: [
 *    {
 *      backgroundColor: "#ff0000",
 *      time: 0,
 *    },
 *    {
 *      backgroundColor: "#00ff00",
 *      time: 50,
 *    },
 *    {
 *      backgroundColor: "#0000ff",
 *      time: 100,
 *    },
 *  ]
 *}
 */
type AndroidKeyFrames = Record<StyleSelectorName.KEYFRAMES, Record<string, Array<{
    time: number;
    [key: string]: StyleValue;
}>>>;
type AndroidStyle = Partial<AndroidStyleItem & AndroidFontFace & AndroidMedia & AndroidKeyFrames>;
export { StyleValue, AndroidStyleItem, AndroidFontFace, AndroidMedia, AndroidKeyFrames, AndroidStyle };
