import { ExpressionStatement } from 'ts-morph';
/**
 * 表示xts中一个完整的节点
 */
interface IElement {
    /**
     * 表达式列表。一个表达式，可能包含多个节点；例如 a().width().height()是1个表达式，但包含3个节点
     *
     * @see 单个节点，请参考： './IExpressNode.ts'
     */
    expressList: ExpressionStatement[];
    /**
     * 子元素
     */
    children: IElement[];
}
export default IElement;
