export interface IAttributeExpression {
    name: string;
    type?: IType;
    title?: string;
    desc?: string;
    defaultValue?: string;
}
export interface IComponentExpression extends IAttributeExpression {
    attibuteGroups: IAttributeExpression[];
    eventGroups?: [];
    methodGroups?: IAttributeExpression[];
}
/**
 * ISubType
 */
export interface ISubType {
    subType: IType;
    children?: IAttributeExpression[];
    subTypeDesc?: string;
    defaultValue?: string;
    isRequired?: boolean;
}
export type IType = ISubType[] | string;
