/**
 * 项目类型
 */
declare enum ProjectType {
    VELA_UX = "velaUx",
    ANDDROID_UX = "androidUx",
    VELA_XTS = "velaXts"
}
declare namespace ProjectType {
    /**
     * 获取项目类型
     *
     * 1. 存在`app/app.xts`：velaXts项目
     * 2. 存在`src/app.ux`
     *    1. `src/manifest.json`文件的 `deviceTypeList` 字段包含`watch`：velaUx
     *    2. 否则：androidUx
     * 3. 否则：velaUx
     * @param projectPath
     * @returns
     */
    function getProjectType(projectPath: string, src?: string): ProjectType;
}
export default ProjectType;
