/**
 * 节点位置数据结构
 */
interface IPosition {
    /**
     * 起始位置
     */
    pos: number;
    /**
     * 结束位置
     */
    end: number;
    /**
     * 起始行
     */
    startLine: number;
    /**
     * 起始列
     */
    startColumn: number;
    /**
     * 结束行
     */
    endLine: number;
    /**
     * 结束列
     */
    endColumn: number;
    /**
     * 节点所在文件
     */
    source?: string;
}
export default IPosition;
