import Router from 'koa-router';
/**
 * IRouter
 */
interface IRouteItem<STateT = any, CustomT = {}> {
    /**
     * 路径
     */
    path: string;
    /**
     * 请求方法, 默认get
     */
    method?: 'get' | 'post' | 'put' | 'delete';
    /**
     * 处理函数
     */
    handler: Router.IMiddleware<STateT, CustomT>;
}
interface IRouter<Param = any> {
    /**
     * 路由的全局参数
     */
    param: Param;
    /**
     * 路由列表
     */
    routeList: IRouteItem[];
}
export { IRouteItem };
export default IRouter;
