import { Schema, SomeJTDSchemaType } from 'ajv/dist/core';
export type JSONSchemaType = Schema & SomeJTDSchemaType;
/**
 * 不好分类的工具函数
 */
declare class CommonUtil {
    /**
     * 根据 buffer 生成 hash 值
     *
     * 用于唯一标识流内容，通常用于文件校验
     * @param buffer
     * @returns hash 值
     */
    static calcDataDigest(buffer: Buffer): Buffer;
    static calcImageDigest(filePath: string): string;
    /**
     * 从开发者项目的 node_module 中加载指定依赖；
     * 如果不存在，提示下载；存在则返回加载的依赖
     * @param packageName
     */
    static requireNodeModule(projectPath: string, packageName: string): Promise<any>;
    static requireModule(path: string, throwError?: boolean): any;
    /**
     * 验证 json 内容
     * @param json json 内容
     * @param schema 验证配置, 详见：https://github.com/ajv-validator/ajv
     *
     *
     * @example
     * ```
     * CommonUtil.validateJson({a:123},
     * {
     *  a:{
     *     type:'number'
     *   }
     * }
     * )
     * ```
     * @returns
     */
    static validateJson(json: JSON, schema: JSONSchemaType): Error[] | undefined;
    static outputQRCodeOnTerminal(text: string): Promise<void>;
}
export default CommonUtil;
