/**
 * StringUtil
 */
declare class StringUtil {
    /**  使用正则表达式去除开头和结尾的 ' " ` */
    static removeQuotes(str: string): string;
    /**
     * 中划线文本转为驼峰
     *
     * aaa-bbb 转换为 aaaBbb
     * @param value
     * @returns
     */
    static hyphenedToCamel(value: string): string;
    /** 首字母大写 */
    static capitalizeFirstLetter(str: string): string;
    /** 逗号分割的字符串转数组 */
    static string2arrayByComma(value?: string): string[];
    /**
     * 数组转字符串
     * @param arr
     * @param styleTransform 是否为style解析树的数组转换
     * @returns
     */
    static arrayToString(arr: any[]): string;
    /**
     * 对象转字符串
     * @param obj
     * @param styleTransform 是否为style解析树的数组转换
     * @returns
     */
    static objectToString(obj: {
        [key: string]: any;
    }): string;
}
export default StringUtil;
