import { ICommand, IParam, PersistentCommand } from '@aiot-toolkit/commander';
/**
 * IStarter
 */
export default abstract class IStarter<O = any> {
    protected name: string;
    protected description: string;
    /**
     * start 的参数列表
     */
    readonly params: IParam[];
    constructor(name: string, description: string);
    get waiter(): PersistentCommand | undefined;
    /**
     * start 的命令
     */
    getCommond(): ICommand;
    /**
     * 运行项目
     * @param projectPath 项目路径
     * @param options 命令参数
     */
    abstract start(projectPath: string, options: O): void | Promise<void>;
}
